        setForm(true);
    }

    return(
        <div>


            <div className="rght_btn">
             <span className="rght_btn_icon"><img src="images/btn_iconb.png" alt="up" /></span>
             <span className="btn_sep"><img src="images/btn_sep.png" alt="sep" /></span>

              <a href="#" onClick={createform}>Upload Post</a> 

              </div> 
              <div>
              {/* style={{display: "none"}} */}
             form? <form action="/Timeline" method="post" enctype="multipart/form-data" onSubmit={submitbtn}>
