import React, { useState, useEffect } from "react";
import axios from "axios";


function UploadTimeline() {
		const [name, setname] = useState("");
		const [image, setimage] = useState();
		const [dataReceived, setdataReceived] = useState({});
		const [Form, setForm] = useState("");
		const [post,setPost] = useState([])

	let submitbtn = e => {
		// console.log(post);
		e.preventDefault();

//fetching image name after data received from axios get call

		post.map(Aray=>{

			console.log(Aray.image)

	})

	//send image to backend node 
		const data = new FormData();
		data.append("name", name);
		data.append("image", image);

		axios.post("http://localhost:4444/Timeline", data).then(response => {
			setdataReceived(response);

		});
	};


	//Get AXIOS CALL FETCH IMAGE DETAILS

useEffect(()=>{
	    axios.get('http://localhost:4444/Timeline').then((response)=>{
			console.log(response.data);
			setPost(response.data)

	    })
	},[])


	const createform = () => {
		setForm(true);
	};

	const hideform = () => {
		setForm(false);
	}


	return (
		<div>
			<div className='rght_btn'>
				<span className='rght_btn_icon'>
					<img src='images/btn_iconb.png' alt='up' />
				</span>
				<span className='btn_sep'>
					<img src='images/btn_sep.png' alt='sep' />
				</span>

				<a href='#' onClick={createform}>
					Upload Post
				</a>

			</div>

			{Form ?(
			<form action='/Timeline' method='post' enctype='multipart/form-data' onSubmit={submitbtn}>
				<label />
				NAME:
				<input
					type='text'
					id='name'
					onChange={e => {
						const { value } = e.target;
						setname(value);
					}}
				/>
				<input
					type='file'
					id='file'
					name='image'
					onChange={e => {
						setimage(e.target.files[0]);
					}}
				/>
				<input name='image' type='submit'  onClick={submitbtn}  />
				 {Form ?(<input name="done" type="submit" value="Done" onClick={hideform} />):(true)}
				{/* <input name='image' type='submit' onClick={submitbtn} /> */}
			</form>)
			:(false )}
			<h1>{dataReceived.data}</h1>

		</div>
	);
}

export default UploadTimeline;
